import React from "react";
// <span className="inline align-baseline"> {date} </span>
const ExperienceCard = ({
  experience: { title, description, tags, link, company },
}) => {
  return (
    <div className="group w-full sm:w-1/2 m-4 mx-auto p-6 rounded-xl border-2 border-gray-300">
      <a href={link}>
        <h1 className="text-xl space-x-2 text-center font-bold">
          {title} at <img src={company} width="35" alt="company" /> 
        </h1>
      </a>
      <hr className="my-4" />
      <p className="">{description}</p>
      <div className="mt-4 mb-8 flex flex-wrap justify-center items-center gap-2">
        {tags.map((tag) => (
          <div className="px-4 py-1 border-2 rounded-full">{tag}</div>
        ))}
      </div>
    </div>
  );
};

export default ExperienceCard;
